package 动态规划.字符串;

//313场周赛


//给你一个仅由小写英文字母组成的字符串 s 。在一步操作中，你可以：
//
//删除 整个字符串 s ，或者
//对于满足1 <= i <= s.length / 2 的任意 i ，如果 s 中的 前 i 个字母和接下来的 i 个字母 相等 ，删除 前 i 个字母。
//返回删除 s 所需的最大操作数。


import java.util.Arrays;

//超时 113/128
public class _对字符串可执行的最大删除数 {
    public static int deleteString(String s) {
        int n = s.length();
        var dp = new int[n];
        //dp[i] : 删除s[0:i]的最大删除次数
        Arrays.fill(dp, 1);
        for (int i = n - 1; i >= 0; i--) {
            int curLen = n - i;
            //len代表去匹配字符串的长度
            for (int len = 1; len <= curLen / 2; len++) {
                if (s.substring(i, i + len).equals(s.substring(i + len, i + len * 2))) {
                    dp[i] = Math.max(dp[i], dp[i + len] + 1);
                }
            }
        }
        return dp[0];
    }

    public static void main(String[] args) {
        int x = deleteString("");
        System.out.println(x);
    }
}


////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
//以上的超时是因为 字符串判等 耗时大
//我们需要快速判断两个子串是否相同。这可以用 O(n^2)O(n2) 的 DP  lcp预处理出来
class dwsx {
    public int deleteString(String S) {
        var s = S.toCharArray();
        var n = s.length;
        // lcp[i][j] 表示 s[i:] 和 s[j:] 的最长公共前缀
        var lcp = new int[n + 1][n + 1];
        for (int i = n - 1; i >= 0; i--) {
            for (int j = n - 1; j >= 0; j--) {
                if (s[i] == s[j]) lcp[i][j] = lcp[i + 1][j + 1] + 1;
            }
        }
        var dp = new int[n];
        //dp[i] : 删除s[0:i]的最大删除次数
        Arrays.fill(dp, 1);
        for (int i = n - 1; i >= 0; i--) {
            for (int len = 1; i + len * 2 <= n; len++) {
                if (lcp[i][i + len] >= len) dp[i] = Math.max(dp[i], dp[i + len] + 1);
            }
        }
        return dp[0];
    }
}
